const videoIsUnderTimeLimit = ({ duration }) => {
  const MAX_VIDEO_LENGTH_SECONDS = 600; // 10 minutes
  return duration <= MAX_VIDEO_LENGTH_SECONDS * 1000;
};

export const getVideoFromCameraRoll = async () => {
